"use strict";(globalThis.webpackChunknumerology=globalThis.webpackChunknumerology||[]).push([[636],{6502(e){e.exports=JSON.parse('{"tag":{"label":"schools","permalink":"/docs/tags/schools","allTagsPath":"/docs/tags","count":3,"items":[{"id":"Systems/Chaldean numerology","title":"Chaldean numerology","description":"Chaldean numerology is an ancient system of numerology that originated in Mesopotamia (modern\u2011day Iraq) and is often described as one of the oldest known forms of name\u2011based numerology.\xa0 It focuses on the vibrational energy of letters in a name and how those vibrations influence a person\u2019s character, destiny, and life events.","permalink":"/docs/Systems/Chaldean numerology"},{"id":"Systems/Pythagorean numerology","title":"Pythagorean numerology","description":"Pythagorean numerology is a Western system of numerology based on the ideas of the ancient Greek philosopher and mathematician Pythagoras, who taught that numbers are the fundamental building blocks of reality and carry spiritual or \u201cvibrational\u201d meaning.\xa0 It is also commonly called modern or Western numerology and is one of the most widely used numerology systems today.","permalink":"/docs/Systems/Pythagorean numerology"},{"id":"Systems/Vedic numerology","title":"Vedic numerology","description":"Vedic numerology (also called Indian or Hindu numerology) is a traditional system from the Vedic\u2011astrology tradition of India that interprets numbers derived from your birth date and name as carriers of planetary energy and karmic patterns.\xa0 It treats numbers as vibrations linked to the nine planets (Navagrahas), so each digit from 1 to 9 is associated with a specific planet and set of qualities.","permalink":"/docs/Systems/Vedic numerology"}],"unlisted":false}}')}}]);